import React, { Component } from 'react';
import '../Styles/Profile.css';
import CityData from './CityData';
import axios from 'axios';

class CityProfile extends Component {

  state = {
    cities: null,
    api: "https://api.collegefitfor.me/cities"
  }

  componentDidMount() {
    axios.get(this.state.api)
        .then(res => {
            var cities = res.data;
            this.setState({cities});
        })
  }

  render() {
    if (this.state.cities === null) {
      return <b> Loading ... </b>
    } else {
      const cities = this.state.cities;
      const city_name = this.props.match.params.cityname;
      const curr_city = cities.find(x => x.city_state_name === city_name);

      return (
        <div>
          <CityData city={curr_city} />
        </div>
      );
    }
  }
}

export default CityProfile;
