import React, {Component} from 'react';
import { PieChart } from "react-d3-components";
import axios from "axios";


class BarChartContainer extends Component{

  state = {
    data: {}
  };

  componentDidMount() {
    axios.get('http://localhost:5000/cities/vis/')
        .then(res => {
            const data = res.data;
            this.setState({data});
        })
  }

  render(){

      // console.log(this.state.data);
      var data1= [];
      for(var i = 0; i < this.state.data.length; i++){
        var new_data = {};
        console.log(this.state.data[i].label);
        new_data['x'] = this.state.data[i].label;
        new_data['y'] = this.state.data[i].value;
        data1.push(new_data);
      }


      console.log(data1);



      var data = {
        label: 'somethingA',
        values: data1
      };




    return(
      <PieChart
                data={data}
                width={1200}
                height={1200}
                margin={{top: 10, bottom: 10, left: 100, right: 100}}
                hideLabels={false}
                />

    )
  }

}


export default BarChartContainer;
