import React, { Component } from 'react';
import { Nav } from 'react-bootstrap';
import '../Styles/Profile.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';
import UT from '../static_resources/ut_austin.png';

class UniversityData extends Component {

  getAttractions() {

    const attractions = this.props.univ.restaurants.split(",")
    for (let i = 0; i < attractions.length; i++) {
      // what if attraction name has "-" in the name?
      attractions[i] = attractions[i].substring(0, attractions[i].indexOf('-'))
    }

    return attractions.map((attr) => {
      console.log(attr)
      return (
        <h4>
          <Nav.Link href={"/restaurants/" + attr.replace(/ /g,"_").toLowerCase()}>
            {attr}
          </Nav.Link>
        </h4>
      )
    })

  }

  render() {

    const city = this.props.univ.city_name.replace(/ /g,"-").toLowerCase()
    .concat("_", this.props.univ.state_name.toLowerCase())

    return (
      <div>
        <Navigation />
        <p></p>
        <h1>{this.props.univ.name}</h1>
        <img src={UT} class="cover-image" />
        <p></p>
        <h4> City: 
          <Nav.Link href={"/cities/" + city}>
            {this.props.univ.city_name}, {this.props.univ.state_name}
          </Nav.Link>
        </h4>
        <h4>Number of Undergrads: {this.props.univ.num_undergrads}</h4>
        <h4>Admission Rate: {this.props.univ.admission_rate}%</h4>
        <h4>Tuition: ${this.props.univ.cost_per_year} per yr</h4>
        <h4>Graduation Rate: {this.props.univ.grade_rate}%</h4>
        <h4>Retention Rate: {this.props.univ.retention_rate}%</h4>
        <h4>Percent Men: {this.props.univ.percent_men}%</h4>
        <h4>Percent Women: {this.props.univ.percent_women}%</h4>
        <h4>Attractions: {this.getAttractions()}</h4>
        <p></p>
        <Footer />
      </div>
    );
  }
}

export default UniversityData;
