import React, { Component } from 'react';
import { Nav } from 'react-bootstrap';
import '../Styles/Profile.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';

class RestaurantData extends Component {

  getUniversities() {

    const universities = this.props.rest.universities

    return universities.map((attr) => {
      return (
        <h4>
          <Nav.Link href={"/universities/" + attr.replace(/ /g,"_").toLowerCase()}>
            {attr}
          </Nav.Link>
        </h4>
      )
    })
  }

  render() {
    const city = this.props.rest.city_state_name
    // TODO: replace blank transactions with n/a
    return (
      <div>
        <Navigation />
        <p></p>
        <h1>{this.props.rest.restaurant_name}</h1>
        <img src={this.props.rest.image_url} class="cover-image" />
        <p></p>
        <h4>Price: {this.props.rest.price}</h4>
        <h4>Rating: {this.props.rest.rating} </h4>
        <h4>Review Count: {this.props.rest.review_count}</h4>
        <h4> City: 
          <Nav.Link href={"/cities/" + city}>
            {this.props.rest.city_name}, {this.props.rest.state_name}
          </Nav.Link>
        </h4>
        <h4>Transactions: {this.props.rest.transactions}</h4>
        <h4>Category: {this.props.rest.categories}</h4>
        <h4>Address: {this.props.rest.address}</h4>
        <h4>Universities: {this.getUniversities()}</h4>
        <Footer />
      </div>
    );
  }
}

export default RestaurantData;
