import React, {Component} from 'react';
import axios from "axios";
import PieChart from 'react-d3-components';

class PChart extends Component{

    state = {
      data: {}
    };

    componentDidMount() {
      axios.get('http://localhost:5000/restaurants/vis/')
          .then(res => {
              const data = res.data;
              this.setState({data});
          })
    }  
      render() {

        let data = [
          {
            label: "somethingA",
            values: [
              { x: "SomethingA", y: 10 },
              { x: "SomethingB", y: 4 },
              { x: "SomethingC", y: 3 }
            ]
          }
        ];

          return (


            <PieChart
                data={data}
                width={600}
                height={400}
                margin={{top: 10, bottom: 10, left: 100, right: 100}}
                sort={null}
            />

          );
      }

  }

  export default PChart;
