import React, { Component } from 'react';
import { Nav, CardDeck, Spinner } from 'react-bootstrap';
import '../Styles/Cities.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Card from 'react-bootstrap/Card';
import { api } from '../instance';

export default class Cities extends Component {
  state = {
    cities: null,
    page: 1,
    loading: true,
  };

  componentDidMount() {
    this.fetchCities();
  }

  fetchCities = () => {
    api
      .get('/cities', {
        params: {
          page: this.state.page,
        },
      })
      .then((res) => res.data)
      .then((cities) => {
        this.setState({ cities, loading: false });
      });
  };

  formatNumber = (value) => {
    return value === 0 ? 'N/A' : value.toLocaleString();
  }

  displayCities = () => {
    const { cities } = this.state;
    let rows = [];
    for (let r = 0; r < 5; r++) {
      let children = [];
      for (let c = 0; c < 3; c++) {
        if (cities.length <= c + 3 * r) {
          break;
        }
        var city = cities[c + 3 * r];
        children.push(
          <Card className="cities-card">
            <Nav.Link href={'/cities/' + String(city.city_state_name)}>
              <Card.Img src={city.image_url} variant="top" height="300" />
              <Card.Body>
                <Card.Title>
                  {city.city_name}, {city.state_name}
                </Card.Title>
                <Card.Text>
                  Population: {this.formatNumber(city.population)}
                </Card.Text>
              </Card.Body>
            </Nav.Link>
          </Card>
        );
      }
      rows.push(<br></br>);
      rows.push(<div className="row">{children}</div>);
      if (cities.length < 3 * r) break;
    }
    return rows;
  };

  changePage = (page) => {
    this.setState(
      {
        page: page,
        loading: true,
      },
      () => this.fetchCities()
    );
  };

  render() {
    return (
      <div>
        <Navigation />
        <Jumbotron className="cities-jumbotron"></Jumbotron>
        <p>
          City matters a lot when deciding a University to attend. <br />
          Whether you're a West Coast or East Coast person, prefer a big or
          small city, or looking for a city that has great access to nature,
          there's a city for you!
        </p>
        {this.state.loading ? (
          <Spinner
            animation="border"
            role="status"
            className="Spinner-custom"
          />
        ) : (
          <>
            <CardDeck>{this.displayCities()}</CardDeck>
            <p />
            <nav className="center-nav">
              <ul class="pagination">
                <li class="page-item">
                  <a
                    class="page-link"
                    href="#"
                    onClick={() => this.changePage(1)}
                  >
                    1
                  </a>
                </li>
                <li class="page-item">
                  <a
                    class="page-link"
                    href="#"
                    onClick={() => this.changePage(2)}
                  >
                    2
                  </a>
                </li>
                <li class="page-item">
                  <a
                    class="page-link"
                    href="#"
                    onClick={() => this.changePage(3)}
                  >
                    3
                  </a>
                </li>
                <li class="page-item">
                  <a
                    class="page-link"
                    href="#"
                    onClick={() => this.changePage(4)}
                  >
                    4
                  </a>
                </li>
                <li class="page-item">
                  <a
                    class="page-link"
                    href="#"
                    onClick={() => this.changePage(5)}
                  >
                    5
                  </a>
                </li>
                <li class="page-item">
                  <a
                    class="page-link"
                    href="#"
                    onClick={() => this.changePage(6)}
                  >
                    6
                  </a>
                </li>
                <li class="page-item">
                  <a
                    class="page-link"
                    href="#"
                    onClick={() => this.changePage(7)}
                  >
                    7
                  </a>
                </li>
              </ul>
            </nav>
          </>
        )}
        <p />
        <Footer />
      </div>
    );
  }
}
