import React, { Component } from 'react';
import { Row, Col } from 'react-bootstrap';
import ProfileCard from './ProfileCard';
import { members } from '../static_resources/members';

const styles = {
  memberWrapper: {
    marginTop: '2vh',
    marginBottom: '4vh',
  },
  cardRow: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    justifyContent: 'center',
    margin: '2vh 0vw',
  },
  statsRow: {
    margin: '2vh 5vw',
  },
};

export default class Gitlab extends Component {
  constructor(props) {
    super(props);
    this.state = {
      commits: [],
      issues: [],
    };
  }

  componentDidMount() {
    fetch(
      'https://gitlab.com/api/v4/projects/16968914/repository/commits?per_page=1000&page=1'
    )
      .then((res) => res.json())
      .then((commits) => {
        this.setState({ commits });
      })
      .catch(console.log);

    fetch(
      'https://gitlab.com/api/v4/projects/16968914/issues?per_page=100&page=1'
    )
      .then((res) => res.json())
      .then((issues) => {
        this.setState({ issues });
      })
      .catch(console.log);
  }

  populateCommits = () => {
    // match member.name to commit.author_name
    for (const member of members) {
      var commits = this.state.commits.filter(
        (commit) =>
          commit['author_name'] === member['name'] ||
          commit['author_email'] === member['email']
      );
      member['commitCount'] = commits.length;
    }
  };

  populateIssues = () => {
    // match member.userName to issue.author.username
    for (const member of members) {
      var issues = this.state.issues.filter(
        (issue) =>
          issue['author']['name'] === member['name'] ||
          issue['author']['username'] === member['username'] ||
          (issue['assignee'] !== null &&
            issue['assignee']['username'] === member['userName'])
      );
      member['issueCount'] = issues.length;
    }
  };

  render() {
    this.populateCommits();
    this.populateIssues();

    return (
      <Col>
        <div>
          <h2>Group Members</h2>
          <Col style={styles.memberWrapper}>
            <div style={styles.cardRow}>
              <ProfileCard member={members[0]} />
              <ProfileCard member={members[1]} />
              <ProfileCard member={members[2]} />
            </div>
            <div style={styles.cardRow}>
              <ProfileCard member={members[3]} />
              <ProfileCard member={members[4]} />
              <ProfileCard member={members[5]} />
            </div>
          </Col>
        </div>
        <div style={styles.statsWrapper}>
          <h2>Repository Stats</h2>
          <Row style={styles.statsRow}>
            <Col>
              <h3>{this.state.commits.length} Commits</h3>
            </Col>
            <Col>
              <h3>{this.state.issues.length} Issues</h3>
            </Col>
            <Col>
              <h3>0 Unit Tests</h3>
            </Col>
          </Row>
        </div>
      </Col>
    );
  }
}
