import React, { Component } from 'react';
import '../Styles/Profile.css';
import RestaurantData from './RestaurantData';
import axios from 'axios';

class RestaurantProfile extends Component {

  state = {
    restaurant: null,
  }

  componentDidMount() {
    const restaurant_name = this.props.match.params.restaurantname.replace(/_/g,"%20")
    .replace(/&/g,"%26")
    axios.get("https://api.collegefitfor.me/restaurants?name=" + restaurant_name)
        .then(res => {
            var restaurant = res.data;
            this.setState({restaurant});
        })
  }

  render() {
    if (this.state.restaurant === null) {
      return <b> Loading ... </b>
    } else {
      return (
        <div>
          <RestaurantData rest={this.state.restaurant[0]} />
        </div>
      ); 
    }
  }
}

export default RestaurantProfile;
