import React, { Component } from 'react';
import { Nav } from 'react-bootstrap';
import '../Styles/Profile.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';

class CityData extends Component {

  getModels = (model) => {

    var models = []
    if (model === "universities") {
      models = this.props.city.universities
    } else {
      models = this.props.city.restaurants
    }

    return models.map((attr) => {
      if (model === "universities") {
        return (
          <h4>
            <Nav.Link href={"/universities/" + attr.replace(/ /g,"_")}>
              {attr}
            </Nav.Link>
          </h4>
        )
      } else {
        return (
          <h4>
            <Nav.Link href={"/restaurants/" + attr.replace(/ /g,"_").toLowerCase()}>
              {attr.substring(0, attr.lastIndexOf("-"))}
            </Nav.Link>
          </h4>
        )
      }
    })

  }

  render() {
    return (
      <div>
        <Navigation />
        <p></p>
        <h1>{this.props.city.city_name}, {this.props.city.state_name}</h1>
        <img src={this.props.city.image_url} class="cover-image" />
        <p></p>
        <h4>Population: {this.props.city.population.toLocaleString()}</h4>
        <h4>Crime Index: {this.props.city.crime_index.toFixed(2)}</h4>
        <h4>Pollution Index: {this.props.city.pollution_index.toFixed(2)}</h4>
        <h4>Rent Index: {this.props.city.rent_index.toFixed(2)}</h4>
        <h4>Restaurant Price Index: {this.props.city.restaurant_price_index.toFixed(2)}</h4>
        <h4>Traffic Index: {this.props.city.traffic_index.toFixed(2)}</h4>
        <h4>Safety Index: {this.props.city.safety_index.toFixed(2)}</h4>
        <h4>Health Care Index: {this.props.city.health_care_index.toFixed(2)}</h4>
        <h4>Time Zone: {this.props.city.time_zone.replace(/_/g, ' ')}</h4>
        <h4>Attractions: {this.getModels("attractions")}</h4>
        <h4>Universities: {this.getModels("universities")}</h4>
        <Footer />
      </div>
    );
  }
}

export default CityData;
