import React, { Component } from 'react';
import '../Styles/Profile.css';
import UniversityData from './UniversityData';
import axios from 'axios';

class UniversityProfile extends Component {

  state = {
    universities: null,
    api: "https://api.collegefitfor.me/universities"
  }

  componentDidMount() {
    axios.get(this.state.api)
        .then(res => {
            var universities = res.data;
            this.setState({universities});
        })
  }

  render() {
    if (this.state.universities === null) {
      return <b> Loading ... </b>
    } else {
      const universities = this.state.universities;
      const university_name = this.props.match.params.universityname;
      const curr_university = universities.find(x => x.name.replace(/ /g,"_").toLowerCase() === university_name);

      return (
        <div>
          <UniversityData univ={curr_university} />
        </div>
      );
    }
  }
}

export default UniversityProfile;
