import React, { Component } from 'react';
import { Nav } from 'react-bootstrap';
import '../Styles/Profile.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';

class RestaurantData extends Component {

  getUniversities() {

    const universities = this.props.rest.universities

    return universities.map((attr) => {
      return (
        <h4>
          <Nav.Link href={"/universities/" + attr.replace(/ /g,"_")}>
            {attr}
          </Nav.Link>
        </h4>
      )
    })
  }

  formatNumber = (value) => {
    return value === 0 ? 'N/A' : value.toLocaleString();
  }

  formatList = (value) => {
    return value === "" ? 'N/A' : value.replace(/,/g, ', ');
  }

  formatRating = (value) => {
    return value === 0 ? 'N/A' : value;
  }

  formatString = (value) => {
    return value === "" ? 'N/A' : value;
  }

  render() {
    const city = this.props.rest.city_state_name
    return (
      <div>
        <Navigation />
        <p></p>
        <h1>{this.props.rest.restaurant_name}</h1>
        <img src={this.props.rest.image_url} class="cover-image" />
        <p></p>
        <h4>Price: {this.formatString(this.props.rest.price)}</h4>
        <h4>Rating: {this.formatRating(this.props.rest.rating)} </h4>
        <h4>Review Count: {this.formatNumber(this.props.rest.review_count)}</h4>
        <h4> City: 
          <Nav.Link href={"/cities/" + city}>
            {this.props.rest.city_name}, {this.props.rest.state_name}
          </Nav.Link>
        </h4>
        <h4>Transactions: {this.formatList(this.props.rest.transactions)}</h4>
        <h4>Category: {this.formatString(this.props.rest.categories)}</h4>
        <h4>Address: {this.formatString(this.props.rest.address)}</h4>
        <h4>Universities: {this.getUniversities()}</h4>
        <Footer />
      </div>
    );
  }
}

export default RestaurantData;
