import React, { Component } from 'react';
import {
  Nav,
  CardDeck,
  Spinner,
  Button,
  Card,
  ButtonGroup,
} from 'react-bootstrap';
import { api } from '../instance.js';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';
import Highlighter from 'react-highlight-words';

export default class SearchResults extends Component {
  state = {
    models: [],
    loading: true,
    routeModelType: 'cities',
    apiModelType: 'cities',
    searchQuery: '',
  };

  componentDidMount() {
    this.fetchModels();
  }

  fetchModels = (modelType = '') => {
    let urlSplit = String(window.location.href).split('/');
    let searchInfo = urlSplit[urlSplit.length - 1];
    let routeModelType =
      modelType === ''
        ? searchInfo.substring(0, searchInfo.indexOf('?'))
        : modelType;
    let apiModelType = routeModelType;
    if (routeModelType === 'attractions') {
      apiModelType = 'restaurants';
    }
    let searchQuery = searchInfo
      .substring(searchInfo.indexOf('=') + 1)
      .replace(/%20/g, ' ');
    this.setState({ searchQuery, routeModelType, apiModelType });
    api
      .get(`${apiModelType}/search/`, {
        params: {
          search_query: searchQuery,
        },
      })
      .then((res) => res.data)
      .then((models) => {
        this.setState({ models, loading: false });
      })
      .catch(() => {
        this.setState({ loading: false });
      });
  };

  switchModelQuery = (routeModelType) => {
    let apiModelType = routeModelType;
    if (routeModelType === 'attractions') {
      apiModelType = 'restaurants';
    }
    const { searchQuery } = this.state;
    api
      .get(`${apiModelType}/search/`, {
        params: {
          search_query: searchQuery,
        },
      })
      .then((res) => res.data)
      .then((models) => {
        this.setState({ models, routeModelType, apiModelType, loading: false });
        window.location.href = `/search/${routeModelType}?search_query=${searchQuery}`;
      })
      .catch(() => {
        this.setState({ loading: false });
      });
  };

  formatPercent = (value) => {
    return value === 0 ? 'N/A' : value.toFixed(2).toString() + '%';
  };

  formatString = (value) => {
    return value === '' ? 'N/A' : value;
  };

  displayModels = () => {
    const models = this.state.models;
    let rows = [];
    for (let r = 0; r < Infinity; r++) {
      let children = [];
      for (let c = 0; c < 4; c++) {
        if (models.length <= c + 4 * r) {
          break;
        }
        var model = models[c + 4 * r];
        if (this.state.routeModelType === 'cities') {
          let city_name = model.city_name + ', ' + model.state_name;
          let population = 'Population: ' + parseInt(model.population);
          children.push(
            <Card className="cities-card">
              <Nav.Link
                className="city-instance"
                href={'/cities/' + String(model.city_state_name)}
              >
                <Card.Img src={model.image_url} variant="top" height="200" />
                <Card.Body>
                  <Card.Title>
                    <Highlighter
                      searchWords={[this.state.searchQuery]}
                      autoEscape={true}
                      textToHighlight={city_name}
                    />
                  </Card.Title>
                  <Card.Text>
                    <Highlighter
                      searchWords={[this.state.searchQuery]}
                      autoEscape={true}
                      textToHighlight={population.toLocaleString()}
                    />
                  </Card.Text>
                </Card.Body>
              </Nav.Link>
            </Card>
          );
        } else if (this.state.routeModelType === 'universities') {
          let university = model.name;
          let city = 'City: ' + model.city_name + ', ' + model.state_name;
          let acceptance_rate =
            'Acceptance Rate: ' +
            this.formatPercent(parseInt(model.admission_rate));
          children.push(
            <Card className="universities-card">
              <Nav.Link
                className="university-instance"
                href={'/universities/' + String(model.name).replace(/ /g, '_')}
              >
                <Card.Img src={model.image_url} variant="top" height="200" />
                <Card.Body>
                  <Card.Text>
                    <Highlighter
                      searchWords={[this.state.searchQuery]}
                      autoEscape={true}
                      textToHighlight={university}
                    />
                  </Card.Text>
                  <Card.Text>
                    <Highlighter
                      searchWords={[this.state.searchQuery]}
                      autoEscape={true}
                      textToHighlight={city}
                    />
                  </Card.Text>
                  <Card.Text>
                    <Highlighter
                      searchWords={[this.state.searchQuery]}
                      autoEscape={true}
                      textToHighlight={acceptance_rate}
                    />
                  </Card.Text>
                </Card.Body>
              </Nav.Link>
            </Card>
          );
        } else {
          let attraction = model.restaurant_name;
          let category = 'Category: ' + this.formatString(model.categories);
          children.push(
            <Card className="attractions-card">
              <Nav.Link
                className="attraction-instance"
                href={
                  '/attractions/' +
                  String(model.restaurant_name + '*' + model.id)
                    .replace(/ /g, '_')
                    .toLowerCase()
                }
              >
                <Card.Img src={model.image_url} variant="top" height="200" />
                <Card.Body>
                  <Card.Title>
                    <Highlighter
                      searchWords={[this.state.searchQuery]}
                      autoEscape={true}
                      textToHighlight={attraction}
                    />
                  </Card.Title>
                  <Card.Text>
                    <Highlighter
                      searchWords={[this.state.searchQuery]}
                      autoEscape={true}
                      textToHighlight={category}
                    />
                  </Card.Text>
                </Card.Body>
              </Nav.Link>
            </Card>
          );
        }
      }
      rows.push(<br></br>);
      rows.push(<div className="row">{children}</div>);
      if (models.length < 4 * r) break;
    }
    return rows;
  };

  modelSelectionRow = () => (
    <ButtonGroup className="d-flex">
      <Button
        variant="primary"
        id="dropdown-btn"
        onClick={() => this.switchModelQuery('cities')}
      >
        Cities
      </Button>
      <Button
        variant="primary"
        id="dropdown-btn"
        onClick={() => this.switchModelQuery('universities')}
      >
        Universities
      </Button>
      <Button
        variant="primary"
        id="dropdown-btn"
        onClick={() => this.switchModelQuery('attractions')}
      >
        Attractions
      </Button>
    </ButtonGroup>
  );

  render() {
    return (
      <div>
        <Navigation />
        <p></p>
        <h1>Search Results for "{this.state.searchQuery}"</h1>
        {this.modelSelectionRow()}
        {this.state.loading ? (
          <Spinner
            animation="border"
            role="status"
            className="Spinner-custom"
          />
        ) : (
          <CardDeck>{this.displayModels()}</CardDeck>
        )}
        <p />
        <Footer />
      </div>
    );
  }
}
