import React, { Component } from 'react';
import { Nav, CardDeck, Spinner } from 'react-bootstrap';
import '../Styles/Universities.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { api } from '../instance';

export default class Universities extends Component {
  state = {
    universities: null,
    page: 1,
    prev_page: 1,
    loading: true,
  };

  componentDidMount() {
    this.fetchUniversities();
  }

  fetchUniversities = () => {
    api
      .get('/universities', {
        params: {
          page: this.state.page,
        },
      })
      .then((res) => res.data)
      .then((universities) => {
        this.setState({ universities, loading: false });
      });
  };

  formatPercent = (value) => {
    return value === 0 ? 'N/A' : value.toFixed(2).toString() + '%';
  }

  displayUniversities = () => {
    const { universities } = this.state;
    let rows = [];
    for (let r = 0; r < 5; r++) {
      let children = [];
      for (let c = 0; c < 3; c++) {
        if (universities.length <= c + 3 * r) {
          break;
        }
        var university = universities[c + 3 * r];
        children.push(
          <Card className="universities-card">
            <Nav.Link
              href={
                '/universities/' +
                university.name.replace(/ /g,"_")
              }
            >
              <Card.Img src={university.image_url} variant="top" height="300" />
              <Card.Body>
                <Card.Title>{university.name}</Card.Title>
                <Card.Text>
                  City: {university.city_name}, {university.state_name}
                </Card.Text>
                <Card.Text>
                  Acceptance Rate: {this.formatPercent(university.admission_rate)}
                </Card.Text>
              </Card.Body>
            </Nav.Link>
          </Card>
        );
      }
      rows.push(<br></br>);
      rows.push(<div className="row">{children}</div>);
      if (universities.length < 3 * r) break;
    }
    return rows;
  };

  changePage = (event, page, prev_page) => {
    event.preventDefault();

    this.setState(
      {
        page: page,
        prev_page: prev_page,
        loading: true,
      },
      () => this.fetchUniversities()
    );
  };

  displayButtons = () => {
    let buttons = []

    if (this.state.prev_page > 7) {
      buttons.push(
        <Button variant="primary" onClick={(e) => {this.changePage(e, this.state.prev_page - 7, this.state.prev_page - 7)}}>
            &lt;&lt;
        </Button>
      )
    }

    buttons.push(
      <Button variant="primary" onClick={(e) => {this.changePage(e, this.state.prev_page, this.state.prev_page)}}>
        {this.state.prev_page}
      </Button>
    )
    buttons.push(
      <Button variant="primary" onClick={(e) => {this.changePage(e, this.state.prev_page + 1, this.state.prev_page)}}>
        {this.state.prev_page + 1}
      </Button>
    )
    buttons.push(
      <Button variant="primary" onClick={(e) => {this.changePage(e, this.state.prev_page + 2, this.state.prev_page)}}>
        {this.state.prev_page + 2}
      </Button>
    )
    buttons.push(
      <Button variant="primary" onClick={(e) => {this.changePage(e, this.state.prev_page + 3, this.state.prev_page)}}>
        {this.state.prev_page + 3}
      </Button>
    )
    buttons.push(
      <Button variant="primary" onClick={(e) => {this.changePage(e, this.state.prev_page + 4, this.state.prev_page)}}>
        {this.state.prev_page + 4}
      </Button>
    )

    if (this.state.prev_page < 43) {
      buttons.push(
        <Button variant="primary" onClick={(e) => {this.changePage(e, this.state.prev_page + 5, this.state.prev_page)}}>
          {this.state.prev_page + 5}
        </Button>
      )
      buttons.push(
        <Button variant="primary" onClick={(e) => {this.changePage(e, this.state.prev_page + 6, this.state.prev_page)}}>
          {this.state.prev_page + 6}
        </Button>
      )
      buttons.push(
        <Button variant="primary" onClick={(e) => {this.changePage(e, this.state.prev_page + 7, this.state.prev_page + 7)}}>
            &gt;&gt;
        </Button>
      )
    }

    return buttons
  }

  render() {
    return (
      <div>
        <Navigation />
        <Jumbotron className="universities-jumbotron"></Jumbotron>
        <p>
          Find the University that's the best fit for you. <br />
          Explore information on over 5,000 Universities across the country.
        </p>
        {this.state.loading ? (
          <Spinner
            animation="border"
            role="status"
            className="Spinner-custom"
          />
        ) : (
          <>
            <CardDeck>
              {this.displayUniversities(this.state.universities)}
            </CardDeck>
            <p />
            {this.displayButtons()}
          </>
        )}
        <p />
        <Footer />
      </div>
    );
  }
}
