import React, { Component } from 'react';
import { Nav, CardDeck, Spinner } from 'react-bootstrap';
import '../Styles/Attractions.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import AttractionFilters from '../Components/AttractionFilters';
import { api } from '../instance';

export default class Attractions extends Component {
  state = {
    attractions: null,
    page: 1,
    prev_page: 1,
    state: "Select State",
    apiLink: "https://api.collegefitfor.me/restaurants",
    loading: true,
  };

  componentDidMount() {
    this.fetchAttractions();
  }

  /* works but is gacky. Use cleaner approach to access API like fetchAttractions method */
  setter = (apiLink) => {
    this.setState({apiLink})

    axios.get(this.state.apiLink)
       .then(res => {
           const attractions = res.data;
           this.setState({ attractions });
       })
  }

  fetchAttractions = () => {
    api
      .get('/restaurants', {
        params: {
          page: this.state.page,
        },
      })
      .then((res) => res.data)
      .then((attractions) => {
        this.setState({ attractions, loading: false });
      });
  };

  formatString = (value) => {
    return value === "" ? 'N/A' : value;
  }

  displayAttractions = () => {
    const { attractions } = this.state;
    let rows = [];
    for (let r = 0; r < 5; r++) {
      let children = [];
      for (let c = 0; c < 4; c++) {
        if (attractions.length <= c + 4 * r) {
          break;
        }
        var attraction = attractions[c + 4 * r];
        children.push(
          <Card className="attractions-card">
            <Nav.Link className="attraction-instance"
              href={
                '/attractions/' +
                String(attraction.restaurant_name + "*" + attraction.id)
                  .replace(/ /g, '_')
                  .toLowerCase()
              }
            >
              <Card.Img src={attraction.image_url} variant="top" height="200" />
              <Card.Body>
                <Card.Title>{attraction.restaurant_name}</Card.Title>
                <Card.Text>Category: {this.formatString(attraction.categories)}</Card.Text>
              </Card.Body>
            </Nav.Link>
          </Card>
        );
      }
      rows.push(<br></br>);
      rows.push(<div className="row">{children}</div>);
      if (attractions.length < 4 * r) break;
    }
    return rows;
  };

  changePage = (event, page, prev_page) => {
    event.preventDefault();

    this.setState(
      {
        page: page,
        prev_page: prev_page,
        loading: true,
      },
      () => this.fetchAttractions()
    );
  };

  displayButtons = () => {
    let buttons = []

    if (this.state.prev_page > 7) {
      buttons.push(
        <Button variant="outline-primary mr-1" onClick={(e) => {this.changePage(e, this.state.prev_page - 7, this.state.prev_page - 7)}}>
            &lt;&lt;
        </Button>
      )
    }

    buttons.push(
      <Button variant="outline-primary mr-1" onClick={(e) => {this.changePage(e, this.state.prev_page, this.state.prev_page)}}>
        {this.state.prev_page}
      </Button>
    )
    buttons.push(
      <Button variant="outline-primary mr-1" onClick={(e) => {this.changePage(e, this.state.prev_page + 1, this.state.prev_page)}}>
        {this.state.prev_page + 1}
      </Button>
    )

    if (this.state.prev_page < 99) {
      buttons.push(
        <Button variant="outline-primary mr-1" onClick={(e) => {this.changePage(e, this.state.prev_page + 2, this.state.prev_page)}}>
          {this.state.prev_page + 2}
        </Button>
      )
      buttons.push(
        <Button variant="outline-primary mr-1" onClick={(e) => {this.changePage(e, this.state.prev_page + 3, this.state.prev_page)}}>
          {this.state.prev_page + 3}
        </Button>
      )
      buttons.push(
        <Button variant="outline-primary mr-1" onClick={(e) => {this.changePage(e, this.state.prev_page + 4, this.state.prev_page)}}>
          {this.state.prev_page + 4}
        </Button>
      )
      buttons.push(
        <Button variant="outline-primary mr-1" onClick={(e) => {this.changePage(e, this.state.prev_page + 5, this.state.prev_page)}}>
          {this.state.prev_page + 5}
        </Button>
      )
      buttons.push(
        <Button variant="outline-primary mr-1" onClick={(e) => {this.changePage(e, this.state.prev_page + 6, this.state.prev_page)}}>
          {this.state.prev_page + 6}
        </Button>
      )
      buttons.push(
        <Button variant="outline-primary mr-1" onClick={(e) => {this.changePage(e, this.state.prev_page + 7, this.state.prev_page + 7)}}>
            &gt;&gt;
        </Button>
      )
    }

    return buttons
  }

  render() {
    return (
      <div>
        <Navigation />
        <Jumbotron className="attractions-jumbotron"></Jumbotron>
        <h1>Attractions</h1>
        <p className="attractions-header">Find the best things to do in your college town!</p>
        <p>
          Whether you're interested in fine dinning, museums, or parks, there's
          always something for you to do!
        </p>
        {this.state.loading ? (
          <Spinner
            animation="border"
            role="status"
            className="Spinner-custom"
          />
        ) : (
          <>
            <div className='row d-flex justify-content-center'>
              <AttractionFilters setter={this.setter} />
            </div>
            <CardDeck>
              {this.displayAttractions(this.state.attractions)}
            </CardDeck>
            <p />
            {this.displayButtons()}
          </>
        )}
        <p />
        <Footer />
      </div>
    );
  }
}
