import React, { Component } from 'react';
import { Nav } from 'react-bootstrap';
import '../Styles/Profile.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';

class UniversityData extends Component {

  getAttractions() {

    const attractions = this.props.univ.restaurants

    return attractions.map((attr) => {
      return (
        <h4>
          <Nav.Link href={"/restaurants/" + attr.replace(/ /g,"_").toLowerCase()}>
            {attr.substring(0, attr.lastIndexOf("-"))}
          </Nav.Link>
        </h4>
      )
    })

  }

  formatNumber = (value) => {
    return value === 0 ? 'N/A' : value.toLocaleString();
  }

  formatPercent = (value) => {
    return value === 0 ? 'N/A' : value.toFixed(2).toString() + '%';
  }

  formatTuition = (value) => {
    return value === 0 ? 'N/A' : '$' + value.toFixed().toString() + '/year';
  }

  formatScore = (value) => {
    return value === 0 ? 'N/A' : value;
  }

  render() {
    const city = this.props.univ.city_state_name
    return (
      <div>
        <Navigation />
        <p></p>
        <h1>{this.props.univ.name}</h1>
        <img src={this.props.univ.image_url} class="cover-image" />
        <p></p>
        <h4> City: 
          <Nav.Link href={"/cities/" + city}>
            {this.props.univ.city_name}, {this.props.univ.state_name}
          </Nav.Link>
        </h4>
        <h4>Number of Undergrads: {this.formatNumber(this.props.univ.num_undergrads)}</h4>
        <h4>Admission Rate: {this.formatPercent(this.props.univ.admission_rate)}</h4>
        <h4>Tuition: {this.formatTuition(this.props.univ.cost_per_year)}</h4>
        <h4>Graduation Rate: {this.formatPercent(this.props.univ.grad_rate)}</h4>
        <h4>Retention Rate: {this.formatPercent(this.props.univ.retention_rate)}</h4>
        <h4>Percent Men: {this.formatPercent(this.props.univ.percent_men)}</h4>
        <h4>Percent Women: {this.formatPercent(this.props.univ.percent_women)}</h4>
        <h4>Average SAT: {this.formatScore(this.props.univ.avg_sat_score)}</h4>
        <h4>Average ACT: {this.formatScore(this.props.univ.avg_act_score)}</h4>
        <h4>Attractions: {this.getAttractions()}</h4>
        <p></p>
        <Footer />
      </div>
    );
  }
}

export default UniversityData;
