import React, { Component } from 'react';
import { Nav, CardDeck } from 'react-bootstrap';
import '../Styles/Restaurants.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Card from 'react-bootstrap/Card';
import { api } from '../instance';

export default class Restaurants extends Component {
  state = {
    restaurants: null,
    page: 1,
  };

  componentDidMount() {
    this.fetchRestaurants();
  }

  fetchRestaurants = () => {
    api
      .get('/restaurants', {
        params: {
          page: this.state.page,
        },
      })
      .then((res) => res.data)
      .then((restaurants) => {
        this.setState({ restaurants });
      });
  };

  displayRestaurants = () => {
    const { restaurants } = this.state;
    let rows = [];
    for (let r = 0; r < 5; r++) {
      let children = [];
      for (let c = 0; c < 3; c++) {
        if (restaurants.length <= c + 3 * r) {
          break;
        }
        var restaurant = restaurants[c + 3 * r];
        children.push(
          <Card className="restaurants-card">
            <Nav.Link
              href={
                '/restaurants/' +
                String(restaurant.restaurant_name)
                  .replace(/ /g, '_')
                  .toLowerCase()
              }
            >
              <Card.Img src={restaurant.image_url} variant="top" height="300" />
              <Card.Body>
                <Card.Title>{restaurant.restaurant_name}</Card.Title>
                <Card.Text>Category: {restaurant.categories}</Card.Text>
              </Card.Body>
            </Nav.Link>
          </Card>
        );
      }
      rows.push(<br></br>);
      rows.push(<div className="row">{children}</div>);
      if (restaurants.length < 3 * r) break;
    }
    return rows;
  };

  changePage = (page) => {
    this.setState(
      {
        page: page,
      },
      () => this.fetchRestaurants()
    );
  };

  render() {
    if (this.state.restaurants === null) {
      return <b> Loading ... </b>;
    } else {
      return (
        <div>
          <Navigation />
          <Jumbotron className="restaurants-jumbotron"></Jumbotron>
          <p>
            Food Matters. Plain and Simple.
            <br />
            Food fuels us. Food makes us.
            <br />
            From the simpliest meals to the finest dining, food is an
            unavoidable part of life.
            <br />
            With food being such a large part of life, it is important to find
            the right restaurant.
            <br />
            Let us help you find the perfect restaurant!
          </p>
          <CardDeck>{this.displayRestaurants(this.state.restaurants)}</CardDeck>
          <p></p>
          <nav className="center-nav">
            <ul class="pagination">
              <li class="page-item">
                <a
                  class="page-link"
                  href="#"
                  onClick={() => this.changePage(1)}
                >
                  1
                </a>
              </li>
              <li class="page-item">
                <a
                  class="page-link"
                  href="#"
                  onClick={() => this.changePage(2)}
                >
                  2
                </a>
              </li>
              <li class="page-item">
                <a
                  class="page-link"
                  href="#"
                  onClick={() => this.changePage(3)}
                >
                  3
                </a>
              </li>
              <li class="page-item">
                <a
                  class="page-link"
                  href="#"
                  onClick={() => this.changePage(4)}
                >
                  4
                </a>
              </li>
            </ul>
          </nav>
          <Footer />
        </div>
      );
    }
  }
}
