import React, { Component } from 'react';
import { Nav, CardDeck } from 'react-bootstrap';
import '../Styles/Cities.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Card from 'react-bootstrap/Card';
import axios from 'axios';

class Cities extends Component {

  state = {
    cities: null,
    page: 1,
    api: "https://api.collegefitfor.me/cities"
  }

  componentDidMount() {
    axios.get(this.state.api)
        .then(res => {
            var cities = res.data;
            this.setState({cities});
        })
  }

  setter = (api) => {
    this.setState({api})

    axios.get(this.state.api)
      .then(res => {
          var cities = res.data;
          this.setState({ cities });
      })
  }

  displayCities = (cities) => {
    let rows = [];
    for (let r = 0; r < 5; r++) {
      let children = [];
      for (let c = 0; c < 3; c++) {
        if (cities.length <= c + 3*r) {
          break
        }
        var city = cities[c + (3*r)];
        console.log(city)
        children.push(
          <Card className="cities-card">
            <Nav.Link href={"/cities/" + String(city.city_state_name)}>
              <Card.Img src={city.image_url} variant="top" height="300" />
              <Card.Body>
                <Card.Title>{city.city_name}, {city.state_name}</Card.Title>
              </Card.Body>
            </Nav.Link>
          </Card>
        )
      }
      rows.push(<br></br>);
      rows.push(<div className="row">{children}</div>);
      if (cities.length < 3 * r) break
    }
    return rows
  };

  changePage = (page) => {
    const cities = [{"name": "sanfrancisco_ca", "median_age": "38.5", "house_price": "$1,119,000"}, 
    {"name": "seattle_wa", "median_age": "35.5", "house_price": "$679,000"}, {"name": "austin_tx", 
    "median_age": "31.5", "house_price": "$392,000"}, {"name": "portland_or", "median_age": "34.5", 
    "house_price": "$492,000"}, {"name": "boulder_co", "median_age": "37.5", "house_price": "$707,000"},
    {"name": "losangeles_ca", "median_age": "31.5", "house_price": "$529,000"}, 
    {"name": "newyork_ny", "median_age": "30.5", "house_price": "$502,000"}, 
    {"name": "lasvegas_nv", "median_age": "32.5", "house_price": "$199,000"}, 
    {"name": "phoenix_az", "median_age": "36.5", "house_price": "$228,000"},
    {"name": "dallas_tx", "median_age": "35.5", "house_price": "$288,000"},
    {"name": "houston_tx", "median_age": "35.5", "house_price": "$268,000"},
    {"name": "boston_ma", "median_age": "32.5", "house_price": "$489,000"}]

    const indexOfLastPost = page * 3;
    const indexOfFirstPost = indexOfLastPost - 3;
    const currentCities = cities.slice(indexOfFirstPost, indexOfLastPost);
    this.setState({cities: currentCities})
    this.setState({page: page})
  }

  render() {
    if (this.state.cities === null) {
      return <b> Loading ... </b>
    } else {
      return (
        <div>
          <Navigation />
          <Jumbotron className="cities-jumbotron"></Jumbotron>

          <p>
            City matters a lot when deciding a University to attend. <br />
            Whether you're a West Coast or East Coast person, prefer a big or
            small city, or looking for a city that has great access to nature,
            there's a city for you!
          </p>

          <CardDeck>
            {this.displayCities(this.state.cities)}
          </CardDeck>

          <p></p>
          <nav className="center-nav">
            <ul class="pagination">
              <li class="page-item"><a class="page-link" href="#" onClick={() => this.changePage(1)}>1</a></li>
              <li class="page-item"><a class="page-link" href="#" onClick={() => this.changePage(2)}>2</a></li>
              <li class="page-item"><a class="page-link" href="#" onClick={() => this.changePage(3)}>3</a></li>
              <li class="page-item"><a class="page-link" href="#" onClick={() => this.changePage(4)}>4</a></li>
            </ul>
          </nav>
          <p></p>
          <Footer />
        </div>
      );
    }
  }
}

export default Cities;
