import React, { Component } from 'react';
import { Nav, CardDeck, Spinner } from 'react-bootstrap';
import '../Styles/Restaurants.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { api } from '../instance';

export default class Restaurants extends Component {
  state = {
    restaurants: null,
    page: 1,
    loading: true,
  };

  componentDidMount() {
    this.fetchRestaurants();
  }

  fetchRestaurants = () => {
    api
      .get('/restaurants', {
        params: {
          page: this.state.page,
        },
      })
      .then((res) => res.data)
      .then((restaurants) => {
        this.setState({ restaurants, loading: false });
      });
  };

  formatString = (value) => {
    return value === "" ? 'N/A' : value;
  }

  displayRestaurants = () => {
    const { restaurants } = this.state;
    let rows = [];
    for (let r = 0; r < 5; r++) {
      let children = [];
      for (let c = 0; c < 3; c++) {
        if (restaurants.length <= c + 3 * r) {
          break;
        }
        var restaurant = restaurants[c + 3 * r];
        children.push(
          <Card className="restaurants-card">
            <Nav.Link
              href={
                '/restaurants/' +
                String(restaurant.restaurant_name + "-" + restaurant.id)
                  .replace(/ /g, '_')
                  .toLowerCase()
              }
            >
              <Card.Img src={restaurant.image_url} variant="top" height="300" />
              <Card.Body>
                <Card.Title>{restaurant.restaurant_name}</Card.Title>
                <Card.Text>Category: {this.formatString(restaurant.categories)}</Card.Text>
              </Card.Body>
            </Nav.Link>
          </Card>
        );
      }
      rows.push(<br></br>);
      rows.push(<div className="row">{children}</div>);
      if (restaurants.length < 3 * r) break;
    }
    return rows;
  };

  changePage = (event, page) => {
    event.preventDefault();

    this.setState(
      {
        page: page,
        loading: true,
      },
      () => this.fetchRestaurants()
    );
  };

  displayButtons = (page) => {
    let buttons = []

    if (page != 1) {
      buttons.push(
        <Button variant="primary" onClick={(e) => {this.changePage(e, 1)}}>
            First
        </Button>
      )
      buttons.push(
        <Button variant="primary" onClick={(e) => {this.changePage(e, this.state.page - 1)}}>
            &lt;&lt;
        </Button>
      )
    }

    buttons.push(
      <Button variant='primary'>
      {page}
      </Button>
    )

    if(page != 134){
      buttons.push(
        <Button variant="primary" onClick={(e) => {this.changePage(e, this.state.page + 1)}}>
            &gt;&gt;
        </Button>
      )
      buttons.push(
        <Button variant="primary" onClick={(e) => {this.changePage(e, 47)}}>
            Last
        </Button>
      )
    }

    return buttons
  }

  render() {
    return (
      <div>
        <Navigation />
        <Jumbotron className="restaurants-jumbotron"></Jumbotron>
        <p>
          Food Matters. Plain and Simple.
          <br />
          Food fuels us. Food makes us.
          <br />
          From the simpliest meals to the finest dining, food is an unavoidable
          part of life.
          <br />
          With food being such a large part of life, it is important to find the
          right restaurant.
          <br />
          Let us help you find the perfect restaurant!
        </p>
        {this.state.loading ? (
          <Spinner
            animation="border"
            role="status"
            className="Spinner-custom"
          />
        ) : (
          <>
            <CardDeck>
              {this.displayRestaurants(this.state.restaurants)}
            </CardDeck>
            <p />
            {this.displayButtons(this.state.page)}
          </>
        )}
        <p />
        <Footer />
      </div>
    );
  }
}
