import React from 'react';
import Button from 'react-bootstrap/Button'
import axios from 'axios';
import { Dropdown, ButtonToolbar } from "react-bootstrap";

class CityFilters extends React.Component {
	state = {
	    cities: null,
		state_filter: "Select State",
		apiLink: "",
	}

	constructor(props) {
        super(props);
    }

	render() {

		return (
		<ButtonToolbar className="CitiesFilter">
			<Dropdown className='m-1' id='dropdown-btn'>
				<Dropdown.Toggle variant="success" id="dropdown-basic">
					{this.state.state_filter}
				</Dropdown.Toggle>
				<Dropdown.Menu className='ourdropdown' id='dropdown-options'>
					{["Alabama", "Alaska", "Arizona", "Arkansas", "California", "Colorado", "Connecticut", "Deleware", "Florida", "Georgia", "Hawaii", "Idaho", "Illinois", "Indiana", "Iowa", "Kansas", "Kentucky", "Louisiana", "Maine", "Maryland", "Massachusetts", "Michigan", "Minnesota", "Mississippi", "Missouri", "Montanat", "Nebraska", "Nevada", "New Hampshire", "New Jersey", "New Mexico", "New York", "North Carolina", "North Dakota", "Ohio", "Oklahoma", "Oregon", "Pennsylvania", "Rhode Island", "South Carolina", "South Dakota", "Tennessee", "Texas", "Utah", "Vermont", "Virginia", "Washington", "West Virginia", "Wisconsin", "Wyoming"].map(value => {
					return (
						<Dropdown.Item
						key={value}
						onClick={() => {
							this.setState({ state_filter : value });
						}}
						>
						{value}
						</Dropdown.Item>
					);
					})}
				</Dropdown.Menu>
				</Dropdown>

			<Button className='m-1' id='go-btn'
				onClick={() => {
					axios.get(`https://api.collegefitfor.me/cities/filter/?state_name=` + this.state.state_filter + '&page=1')
					.then(res => {
						const cities = res.data;
						this.setState({ cities });
						this.props.setter(`https://api.collegefitfor.me/cities/filter/?state_name=` + this.state.state_filter + '&page=1');
					})
				}}
				>
				Go
			</Button>
		</ButtonToolbar>
		);
	}
}

export default CityFilters;
