import React, { Component } from 'react';
import '../Styles/Profile.css';
import RestaurantData from './RestaurantData';
import axios from 'axios';

class RestaurantProfile extends Component {

  state = {
    restaurants: null,
    api: "https://api.collegefitfor.me/restaurants"
  }

  componentDidMount() {
    axios.get(this.state.api)
        .then(res => {
            var restaurants = res.data;
            this.setState({restaurants});
        })
  }

  render() {
    if (this.state.restaurants === null) {
      return <b> Loading ... </b>
    } else {
      const restaurants = this.state.restaurants;
      const restaurant_name = this.props.match.params.restaurantname;
      const curr_restaurant = restaurants.find(x => x.restaurant_name.replace(/ /g,"_").toLowerCase() === restaurant_name);

      return (
        <div>
          <RestaurantData rest={curr_restaurant} />
        </div>
      ); 
    }
  }
}

export default RestaurantProfile;
