import React, { Component } from 'react';
import { Nav, CardDeck, Spinner } from 'react-bootstrap';
import '../Styles/Universities.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Card from 'react-bootstrap/Card';
import { api } from '../instance';

export default class Universities extends Component {
  state = {
    universities: null,
    page: 1,
    loading: true,
  };

  componentDidMount() {
    this.fetchUniversities();
  }

  fetchUniversities = () => {
    api
      .get('/universities', {
        params: {
          page: this.state.page,
        },
      })
      .then((res) => res.data)
      .then((universities) => {
        this.setState({ universities, loading: false });
      });
  };

  formatPercent = (value) => {
    return value === 0 ? 'N/A' : value.toFixed(2).toString() + '%';
  }

  displayUniversities = () => {
    const { universities } = this.state;
    let rows = [];
    for (let r = 0; r < 5; r++) {
      let children = [];
      for (let c = 0; c < 3; c++) {
        if (universities.length <= c + 3 * r) {
          break;
        }
        var university = universities[c + 3 * r];
        children.push(
          <Card className="universities-card">
            <Nav.Link
              href={
                '/universities/' +
                university.name.replace(/ /g,"_")
              }
            >
              <Card.Img src={university.image_url} variant="top" height="300" />
              <Card.Body>
                <Card.Title>{university.name}</Card.Title>
                <Card.Text>
                  City: {university.city_name}, {university.state_name}
                </Card.Text>
                <Card.Text>
                  Acceptance Rate: {this.formatPercent(university.admission_rate)}
                </Card.Text>
              </Card.Body>
            </Nav.Link>
          </Card>
        );
      }
      rows.push(<br></br>);
      rows.push(<div className="row">{children}</div>);
      if (universities.length < 3 * r) break;
    }
    return rows;
  };

  changePage = (page) => {
    this.setState(
      {
        page: page,
        loading: true,
      },
      () => this.fetchUniversities()
    );
  };

  render() {
    return (
      <div>
        <Navigation />
        <Jumbotron className="universities-jumbotron"></Jumbotron>
        <p>
          Find the University that's the best fit for you. <br />
          Explore information on over 5,000 Universities across the country.
        </p>
        {this.state.loading ? (
          <Spinner
            animation="border"
            role="status"
            className="Spinner-custom"
          />
        ) : (
          <>
            <CardDeck>
              {this.displayUniversities(this.state.universities)}
            </CardDeck>
            <p />
            <nav className="center-nav">
              <ul class="pagination">
                <li class="page-item">
                  <a
                    class="page-link"
                    href="#"
                    onClick={() => this.changePage(1)}
                  >
                    1
                  </a>
                </li>
                <li class="page-item">
                  <a
                    class="page-link"
                    href="#"
                    onClick={() => this.changePage(2)}
                  >
                    2
                  </a>
                </li>
                <li class="page-item">
                  <a
                    class="page-link"
                    href="#"
                    onClick={() => this.changePage(3)}
                  >
                    3
                  </a>
                </li>
                <li class="page-item">
                  <a
                    class="page-link"
                    href="#"
                    onClick={() => this.changePage(4)}
                  >
                    4
                  </a>
                </li>
              </ul>
            </nav>
          </>
        )}
        <p />
        <Footer />
      </div>
    );
  }
}
