import React, { Component } from 'react';
import { Nav, CardDeck } from 'react-bootstrap';
import '../Styles/Restaurants.css';
import Navigation from '../Components/Navigation';
import Footer from '../Components/Footer';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Card from 'react-bootstrap/Card';

class Restaurants extends Component {

  state = {
    restaurants: [{"id": 11, "restaurant_name": "Prince Street Pizza", "rating": 4.5,
    "image_url": "https://s3-media3.fl.yelpcdn.com/bphoto/ZAukOyv530w4KjOHC5YY1w/o.jpg",
    "price": "$", "review_count": 3471, "city_name": "New York", "state_name": "NY",
    "transactions": "pickup,delivery", "categories": "Pizza", "address": "27 Prince St",
    "city_state_name": "new-york_new-york"}, {"id": 12, "restaurant_name": "Burger & Lobster",
    "rating": 4.0, "image_url": "https://s3-media3.fl.yelpcdn.com/bphoto/1rHd7F4zztAsScwzM0vDmw/o.jpg", 
    "price": "$$", "review_count": 5439, "city_name": "New York", "state_name": "NY", "transactions": 
    "restaurant_reservation,pickup,delivery", "categories": "Seafood", "address": "39 W 19th St", 
    "city_state_name": "new-york_new-york"}, {"id": 13, "restaurant_name": "Thai Villa", "rating": 4.5, 
    "image_url": "https://s3-media1.fl.yelpcdn.com/bphoto/OgCU9NPFIl8hF_04Zani9w/o.jpg", "price": "$$", 
    "review_count": 3314, "city_name": "New York", "state_name": "NY", "transactions": "pickup,delivery", 
    "categories": "Thai", "address": "5 E 19th St", "city_state_name": "new-york_new-york"}, {"id": 14, 
    "restaurant_name": "Gramercy Tavern", "rating": 4.5, "image_url": 
    "https://s3-media3.fl.yelpcdn.com/bphoto/MgfPMoFNmjIWcrMxTZbsvw/o.jpg", "price": "$$$$", "review_count": 
    2886, "city_name": "New York", "state_name": "NY", "transactions": "", "categories": "American (New)", 
    "address": "42 E 20th St", "city_state_name": "new-york_new-york"}, {"id": 15, "restaurant_name": 
    "The High Line", "rating": 4.5, "image_url": "https://s3-media2.fl.yelpcdn.com/bphoto/sTIQDH9ULKhqy8mpwMPTRw/o.jpg", 
    "price": "$", "review_count": 2329, "city_name": "New York", "state_name": "NY", "transactions": "", "categories": 
    "Parks", "address": "820 Washington St", "city_state_name": "new-york_new-york"}, {"id": 16, "restaurant_name": 
    "Doughnut Plant", "rating": 4.5, "image_url": "https://s3-media1.fl.yelpcdn.com/bphoto/hLWKXsLv6hyltNSilBy8-g/o.jpg", 
    "price": "$$", "review_count": 3202, "city_name": "New York", "state_name": "NY", "transactions": "pickup,delivery", 
    "categories": "Donuts", "address": "379 Grand St", "city_state_name": "new-york_new-york"}, {"id": 17, "restaurant_name": 
    "Peter Luger", "rating": 4.0, "image_url": "https://s3-media4.fl.yelpcdn.com/bphoto/KRpKd1ZdcYSPRDhlb12Vjw/o.jpg", "price": 
    "$$$$", "review_count": 5707, "city_name": "Brooklyn", "state_name": "NY", "transactions": "", "categories": "Steakhouses", 
    "address": "178 Broadway", "city_state_name": "new-york_new-york"}, {"id": 18, "restaurant_name": 
    "National September 11 Memorial Museum", "rating": 4.5, "image_url": "https://s3-media2.fl.yelpcdn.com/bphoto/yoe6Wha7gQlTBNIeFO0UhQ/o.jpg", 
    "price": "$", "review_count": 1417, "city_name": "New York", "state_name": "NY", "transactions": "", "categories": "Museums", "address": 
    "180 Greenwich St", "city_state_name": "new-york_new-york"}, {"id": 19, "restaurant_name": "Shake Shack", "rating": 4.0, "image_url": 
    "https://s3-media3.fl.yelpcdn.com/bphoto/J0UoRJKdaMl0EPdgxYIlKw/o.jpg", "price": "$$", "review_count": 5567, "city_name": "New York", 
    "state_name": "NY", "transactions": "delivery", "categories": "Burgers", "address": "Madison Square Park", "city_state_name": 
    "new-york_new-york"}, {"id": 20, "restaurant_name": "Clinton Street Baking Company", "rating": 4.0, "image_url": 
    "https://s3-media1.fl.yelpcdn.com/bphoto/DWHdizJ9bKbXN4yq6nbwAA/o.jpg", "price": "$$", "review_count": 5053, "city_name": "New York", 
    "state_name": "NY", "transactions": "restaurant_reservation,pickup,delivery", "categories": "Bakeries", "address": "4 Clinton St", 
    "city_state_name": "new-york_new-york"}],
    page: 2,
  }

  displayRestaurants = (restaurants) => {
    let rows = [];
    for (let r = 0; r < 5; r++) {
      let children = [];
      for (let c = 0; c < 3; c++) {
        if (restaurants.length <= c + 3*r) {
          break
        }
        var restaurant = restaurants[c + (3*r)];
        children.push(
          <Card className="restaurants-card">
            <Nav.Link href={"/restaurants/" + String(restaurant.restaurant_name).replace(/ /g,"_").toLowerCase()}>
              <Card.Img src={restaurant.image_url} variant="top" height="300" />
              <Card.Body>
                <Card.Title>{restaurant.restaurant_name}</Card.Title>
                <Card.Text>Category: {restaurant.categories}</Card.Text>
              </Card.Body>
            </Nav.Link>
          </Card>
        )
      }
      rows.push(<br></br>);
      rows.push(<div className="row">{children}</div>);
      if (restaurants.length < 3 * r) break
    }
    return rows
  }

  render() {
    return (
      <div>
        <Navigation />
        <Jumbotron className="restaurants-jumbotron"></Jumbotron>

        <p>
          Food Matters. Plain and Simple.
          <br />
          Food fuels us. Food makes us.
          <br />
          From the simpliest meals to the finest dining, food is an unavoidable
          part of life. 
          <br />
          With food being such a large part of life, it is important to find the right restaurant. 
          <br />
          Let us help you find the perfect restaurant!
        </p>
        <CardDeck>
          {this.displayRestaurants(this.state.restaurants)}
        </CardDeck>
        <p></p>
        <Footer />
      </div>
    );
  }
}

export default Restaurants;
